# Loading libraries and initialization ----

# Install and missing package

library(doParallel)
library(foreach)
library(readxl)
library(readr)
library(tidyverse)
library(seqinr)
library(msa)
library(reticulate)
library(ggpubr)
library(here)
library(scales)
library(RColorBrewer)

# Set the working directory to the repository folder
# setwd("/repo-BetaCov")

ID_THRESH = 0.99
NUM_MAX_MISMATCH = 0
GAPratio = 0.15

VIRUS = "nCov"

source(file = here("Functions", "map.Tepitopes.R"))
source(file = here("Functions", "map.MHCepitopes.R"))
source(file = here("Functions", "map.Bepitopes.R"))
source(file = here("Functions", "map.REFepitopes.R"))
source(file = here("Functions", "find.immunogen.R"))
source(file = here("Functions", "find.single.coverages.R"))

translation_indices <- readRDS(file = here("Data", "translation_indices"))

# Importing the positive-T cell assay based T cell epitopes data -----

ExEpiTcell_full <- as.data.frame(read_excel(here("Data", paste0("Tcell-", VIRUS, "-Results.xls")), 
                                                 sheet = "Results"))

ExEpiTcell_full %>% separate_rows(Host, `Assay Type Category`, `Assay Result`, `MHC Allele Name`, 
                                  `MHC Allele Class`, Method, Measurement, sep = ",") -> ExEpiTcell_full

ExEpiTcell <- ExEpiTcell_full %>% 
  filter(Host == "Human" & 
           str_detect(string = `Assay Result`, pattern = "Positive") &
           str_detect(string = `Assay Type Category`, pattern = "T")) %>%
  # filter(!str_detect(string = `MHC Allele Name`, pattern = "N/A")) %>% 
  # filter(!str_detect(string = `MHC Allele Name`, pattern = "class")) %>%
  select(`IEDB ID`, `Epitope Sequence`, `MHC Allele Name`, `MHC Allele Class`) %>% 
  group_by(`IEDB ID`, `Epitope Sequence`) %>% 
  distinct_at(vars(`MHC Allele Name`), .keep_all = TRUE) %>% 
  summarise(`MHC Allele Names` = paste(`MHC Allele Name`, collapse = "/"), 
            `MHC Allele Classes` = paste(`MHC Allele Class`, collapse = ",")) %>%
  distinct_at(vars(`IEDB ID`), .keep_all = TRUE) %>%
  ungroup()

colnames(ExEpiTcell) <- c("IEDB", "Epitope", "MHC Allele Names", "MHC Allele Classes")

write.fasta(
  sequences = as.list(ExEpiTcell$Epitope), 
  names = ExEpiTcell$IEDB, 
  file.out = here("Data", paste0("Tepitopes-", VIRUS, ".fasta")))

# Mapping positive-T cell assay based T cell epitopes ----

VIRUSES <- c(VIRUS)

PROTEINS <- translation_indices$gene
#c("orf1a", "orf1b", "S", "ORF3a", "E", "M", "ORF6", "ORF7a", "ORF8", "N", "ORF10")

cl <- parallel::makeCluster(5)
doParallel::registerDoParallel(cl)

cases_PROTEINS <- c(rep(PROTEINS, times = length(VIRUSES)))
cases_VIRUSES <- c(rep(VIRUSES, each = length(PROTEINS)))

df_mapping_full <- foreach(i = seq_along(cases_VIRUSES), .combine = 'rbind.data.frame') %dopar% {
  map.Tepitopes(VIRUS = cases_VIRUSES[i], PROTEIN = cases_PROTEINS[i], NUM_MAX_MISMATCH, GAPratio)
}

parallel::stopCluster(cl)

df <- df_mapping_full %>% 
  filter(Id!=0) %>%
  select("Virus", "Protein", "IEDB", "Epitope", "Id", "length", "start", "end") %>% 
  spread(key = Virus, value = Id, fill = 0)

df <- left_join(df, ExEpiTcell, c("IEDB", "Epitope"))

saveRDS(object = df, file = here("Data", paste0("df-allTepitopes-", VIRUS)))

df <- df %>% filter(nCov > ID_THRESH) 

df$Source <- "SARS" #VIRUS

saveRDS(object = df, file = here("Data", paste0("df-Tepitopes-", VIRUS)))

saveRDS(object = df_mapping_full, file = here("Data", paste0("df-fullTepitopes-", VIRUS)))

df_Tepitopes <- readRDS(here("Data", paste0("df-Tepitopes-", VIRUS)))

df_fullTepitopes <- readRDS(here("Data", paste0("df-fullTepitopes-", VIRUS)))

df_allTepitopes <- readRDS(here("Data", paste0("df-allTepitopes-", VIRUS)))



# Importing the positive-MHC assay based T cell epitopes data -----

ExEpiTcell_full <- as.data.frame(read_excel(here("Data", paste0("MHC-", VIRUS, "-Results.xls")), 
                                            sheet = "Results"))

ExEpiTcell_full %>% separate_rows(Host, `Assay Type Category`, `Assay Result`, `MHC Allele Name`, 
                                  `MHC Allele Class`, Method, Measurement, sep = ",") -> ExEpiTcell_full

ExEpiTcell <- ExEpiTcell_full %>% 
  filter(Host == "Human" & 
           str_detect(string = `Assay Result`, pattern = "Positive")) %>% 
  # filter(!str_detect(string = `MHC Allele Name`, pattern = "N/A")) %>% 
  # filter(!str_detect(string = `MHC Allele Name`, pattern = "class")) %>%
  select(`IEDB ID`, `Epitope Sequence`, `MHC Allele Name`, `MHC Allele Class`) %>% 
  group_by(`IEDB ID`, `Epitope Sequence`) %>% 
  distinct_at(vars(`MHC Allele Name`), .keep_all = TRUE) %>% 
  summarise(`MHC Allele Names` = paste(`MHC Allele Name`, collapse = "/"), 
            `MHC Allele Classes` = paste(`MHC Allele Class`, collapse = ",")) %>%
  distinct_at(vars(`IEDB ID`), .keep_all = TRUE) %>%
  ungroup()

colnames(ExEpiTcell) <- c("IEDB", "Epitope", "MHC Allele Names", "MHC Allele Classes")

write.fasta(
  sequences = as.list(ExEpiTcell$Epitope), 
  names = ExEpiTcell$IEDB, 
  file.out = here("Data", paste0("MHCepitopes-", VIRUS, ".fasta")))

# Mapping positive-MHC assay based T cell epitopes ----

VIRUSES <- c(VIRUS)

PROTEINS <- translation_indices$gene 
#c("orf1a", "orf1b", "S", "ORF3a", "E", "M", "ORF6", "ORF7a", "ORF8", "N", "ORF10")

cl <- parallel::makeCluster(5)
doParallel::registerDoParallel(cl)

cases_PROTEINS <- c(rep(PROTEINS, times = length(VIRUSES)))
cases_VIRUSES <- c(rep(VIRUSES, each = length(PROTEINS)))

df_mapping_full <- foreach(i = seq_along(cases_VIRUSES), .combine = 'rbind.data.frame') %dopar% {
  map.MHCepitopes(VIRUS = cases_VIRUSES[i], PROTEIN = cases_PROTEINS[i], NUM_MAX_MISMATCH, GAPratio)
}

parallel::stopCluster(cl)

df <- df_mapping_full %>% 
  filter(Id!=0) %>%
  select("Virus", "Protein", "IEDB", "Epitope", "Id", "length", "start", "end") %>% distinct() %>% 
  spread(key = Virus, value = Id, fill = 0)

df <- left_join(df, ExEpiTcell, c("IEDB", "Epitope"))

saveRDS(object = df, file = here("Data", paste0("df-allMHCepitopes-", VIRUS)))

df <- df %>% filter(nCov > ID_THRESH) 

df$Source <- "SARS" #VIRUS

saveRDS(object = df, file = here("Data", paste0("df-MHC-epitopes-", VIRUS)))

saveRDS(object = df_mapping_full, file = here("Data", paste0("df-fullMHC-epitopes-", VIRUS)))

df_MHCepitopes <- readRDS(here("Data", paste0("df-MHC-epitopes-", VIRUS)))

df_fullMHCepitopes <- readRDS(here("Data", paste0("df-fullMHC-epitopes-", VIRUS)))

df_allMHCepitopes <- readRDS(here("Data", paste0("df-allMHCepitopes-", VIRUS)))

x <- full_join(x = df_MHCepitopes, y = df_Tepitopes %>% 
                 select(IEDB, Epitope, nCov, `MHC Allele Names`, `MHC Allele Classes`, Protein, Source), "IEDB")

x <- x %>% mutate(`MHC Allele Names` = if_else(condition = !is.na(x$`MHC Allele Names.x`), 
                                          true = `MHC Allele Names.x`, 
                                          false = `MHC Allele Names.y`)) 

x <- x %>% mutate(`MHC Allele Classes` = if_else(condition = !is.na(x$`MHC Allele Classes.x`), 
                                               true = `MHC Allele Classes.x`, 
                                               false = `MHC Allele Classes.y`)) 

x <- x %>% mutate(Protein = if_else(condition = !is.na(x$Protein.x), 
                                                 true = Protein.x, 
                                                 false = Protein.y))

x <- x %>% mutate(Source = if_else(condition = !is.na(x$Source.x), 
                                    true = Source.x, 
                                    false = Source.y))

x <- x %>% mutate(Epitope = if_else(condition = !is.na(x$Epitope.x), 
                                   true = Epitope.x, 
                                   false = Epitope.y))

x <- x %>% mutate(nCov = if_else(condition = !is.na(x$nCov.x), 
                                    true = nCov.x, 
                                    false = nCov.y))

x <- x %>% 
  filter(`MHC Allele Names` %>% str_detect(pattern = "N/A", negate = TRUE)) %>% 
  filter(`MHC Allele Names` %>% str_detect(pattern = "class", negate = TRUE)) 
  
suppl_table_1 <- x %>% select(IEDB, Epitope, Protein, `MHC Allele Names`, `MHC Allele Classes`, Source, nCov)

saveRDS(object = suppl_table_1, file = here("Data", paste0("suppl_table_1", VIRUS)))

